 src="<?php echo base_url(); ?>styles/admin/plugins/jQuery/jQuery-2.1.3.min.js"></script>    <!-- Bootstrap 3.3.2 JS -->    <script src="<?php echo base_url(); ?>styles/admin/bootstrap/js/bootstrap.min.js" type="text/javascript"></script>    <!-- iCheck -->    <script src="<?php echo base_url(); ?>styles/admin/plugins/iCheck/icheck.min.js" type="text/javascript"></script>    <script src="<?php echo base_url(); ?>styles/admin/plugins/input-mask/jquery.inputmask.js" type="text/javascript"></script>    <script src="<?php echo base_url(); ?>styles/admin/plugins/input-mask/jquery.inputmask.date.extensions.js" type="text/javascript"></script>    <script src="<?php echo base_url(); ?>styles/admin/plugins/input-mask/jquery.inputmask.extensions.js" type="text/javascript"></script>    <!-- Select picker-->    <script src="<?php echo base_url(); ?>styles/admin/dist/js/bootstrap-select.min.js" type="text/javascript"></script>    <script>      $(function () {          $('.selectpicker').selectpicker('refresh');          $("#txtIngresoUNI").inputmask("dd/mm/yyyy", {"placeholder": "dd/mm/aaaa"});          $("#txtIngresoUNI").attr('pattern', '^([0-9]{2}/[0-9]{2})/[0-9]{4}$');          $('input').iCheck({              checkboxClass: 'icheckbox_square-blue',              radioClass: 'iradio_square-blue',              increaseArea: '20%' // optional          });      });      $("form#solicitudAfiliacion").find('div').each(          //Funcion que asigna de principio el color de los recuadros de cada campo según si es exijido o no          function()          {              var clase = $(this).prop("class");              clases=clase.split(" ");              if ($(this).hasClass('form-group') && clase.substring(0,4)==='ftxt'){                  var id = clases[0].substring(1, clases[0].length);                  var eid = "#"+id;                  var iid = "#i"+id;                  var feid = ".f"+clases[0].substring(1, clases[0].length);                  var req = $(eid).prop('required');                  if(req===true)                  {                      $(feid).attr("class", "f"+id+" form-group has-warning");                      $(iid).attr("class", "fa fa-times-circle-o");                  }                  else                  {                      $(feid).attr("class", "f"+id+" form-group has-success");                      $(iid).attr("class", "fa fa-check");                  }                  $(eid).blur(                      function()                      {                          validarFormato(id);                      }                  );                  $(eid).keyup(                      function()                      {                          validarFormato(id);                      }                  );              }          }      );      function validarFormato(id)      {          var eid = "#"+id;          var feid = ".f"+id;          var iid = "#i"+id;          var ptrn = $(eid).attr("pattern");          var fn = new RegExp(ptrn);          var valor=$(eid).val();          var req = $(eid).prop('required');          if (valor==='' && req===true)          {              $(feid).attr("class", "f"+id+" form-group has-warning");              $(iid).attr("class", "fa fa-times-circle-o");          }          else if(valor==='' && req===false)          {              $(feid).attr("class", "f"+id+" form-group has-success");              $(iid).attr("class", "fa fa-check");          }          else          {              if (fn.test(valor))              {                  $(feid).attr("class", "f"+id+" form-group has-success");                  $(iid).attr("class", "fa fa-check");                  if(id.substring(0,10)==='txtIngreso')                  {                      // REVISAR ESTA PARTE NO ESTA HACIENDO BIEN LAS FECHAS                      // Y NO ESTÁ VALIDANDO POR ENDE                      hoy = new Date().getTime();                      fecha_ingreso = new Date($(eid).prop('value')).getTime();                      console.log(fecha_ingreso);                      if (hoy<fecha_ingreso)                      {                          console.log(fecha_ingreso);                      }                  }              }              else              {                  $(feid).attr("class", "f"+id+" form-group has-error");                  $(iid).attr("class", "fa fa-times-circle-o");              }          }      }    </script>  </body></html>